def logMessage = params['_source'].error?.log?.message;
if (logMessage != null && logMessage != "") {
  emit(logMessage);
  return;
}
def exception = params['_source'].error?.exception;
if (exception != null && exception.isEmpty() == false) {
  def exceptionMessage = exception instanceof Map ? exception?.message : exception[0]?.message;
  if (exceptionMessage instanceof List) {
    exceptionMessage = exceptionMessage[0]
  }
  if (exceptionMessage != null && exceptionMessage != "") {
    emit(exceptionMessage);
  }
}

script.source:
def eventDuration = ctx.event?.duration ?: 0;
def rootObjectName = ctx.processor?.event;
def rootObject = ctx[rootObjectName];
if (rootObject == null) {
  rootObject = [:];
  ctx[rootObjectName] = rootObject;
}
rootObject.duration = ["us": (long)(eventDuration/1000)];

Map dynamic_templates = new HashMap();
for (entry in ctx._metric_descriptions.entrySet()) {
  String name = entry.getKey();
  Map description = entry.getValue();
  String metric_type = description.type;
  if (metric_type == "histogram") {
    dynamic_templates[name] = "histogram_metrics";
  } else if (metric_type == "summary") {
    dynamic_templates[name] = "summary_metrics";
  } else {
    dynamic_templates[name] = "double_metrics";
  }
}
ctx._dynamic_templates = dynamic_templates;
ctx.remove("_metric_descriptions");

Map dynamic_templates = new HashMap();
for (sample in ctx.metricset.samples) {
  String name = sample.name;
  String metric_type = sample.type;
  if (metric_type == "histogram") {
    dynamic_templates[name] = "histogram_metrics";
    ctx.put(name, ["values": sample.values, "counts": sample.counts]);
  } else if (metric_type == "summary") {
    dynamic_templates[name] = "summary_metrics";
    ctx.put(name, ["value_count": sample.value_count, "sum": sample.sum]);
  } else {
    dynamic_templates[name] = "double_metrics";
    ctx.put(name, sample.value);
  }
}
ctx._dynamic_templates = dynamic_templates;
ctx.metricset.remove("samples");
